function showSlide(){if(window.screen.width>=sizeDesktop){slider.style.width="1222px";slider.style.transform="translateX(0)";sliderItems.forEach(n=>n.style.width="auto");return}sliderWidth=sliderContainer.offsetWidth;slider.style.width=sliderWidth*sliderItems.length+"px";sliderItems.forEach(n=>n.style.width=sliderWidth+"px");rollSlider()}function rollSlider(){if(window.screen.width>=sizeDesktop){slider.style.transform="translateX(0)";return}slider.style.transform=`translateX(${-sliderCount*sliderWidth}px)`}function nextSlide(){sliderCount++;buttonPrev.hasAttribute("disabled")===!0?buttonPrev.removeAttribute("disabled"):sliderCount>=sliderItems.length-1&&buttonNext.setAttribute("disabled","");rollSlider();thisSlide(sliderCount)}function prevSlide(){sliderCount--;buttonNext.hasAttribute("disabled")===!0?buttonNext.removeAttribute("disabled"):sliderCount<=0&&buttonPrev.setAttribute("disabled","");rollSlider();thisSlide(sliderCount)}function thisSlide(n){dots.forEach(n=>n.classList.remove("sliderControl__dot--activ"));dots[n].classList.add("sliderControl__dot--activ")}const wrapItems=Array.from(document.querySelectorAll(".slider__wrap-items"));let sizeDesktop=1366,setClassDepScreenWidth=()=>{if(window.screen.width>=sizeDesktop)for(let n of wrapItems)n.classList.remove("slider__item");else for(let n of wrapItems)n.classList.add("slider__item")};setClassDepScreenWidth();window.addEventListener("resize",()=>{setClassDepScreenWidth(),showSlide()});const sliderContainer=document.querySelector(".upgrade__slider-container"),slider=document.querySelector(".upgrade__slider"),sliderItems=document.querySelectorAll(".slider__item"),buttonPrev=document.querySelector("#btnPrev"),buttonNext=document.querySelector("#btnNext"),dots=document.querySelectorAll(".sliderControl__dot");let sliderCount=0,sliderWidth;buttonNext.addEventListener("click",nextSlide);buttonPrev.addEventListener("click",prevSlide);showSlide();dots.forEach((n,t)=>{n.addEventListener("click",()=>{sliderCount=t,rollSlider(),thisSlide(sliderCount)})});
